                    const data = await response.json();
                    const allTools = data.tools || [];
                    
                    toolIds.forEach(tid => {
                        // Handle prefixed IDs (api:xxx, kb:xxx)
                        const cleanId = tid.replace(/^(api:|kb:)/, '');
                        const tool = allTools.find(t => t.id === tid || t.id === cleanId);
                        if (tool) {
                            addToolToSelection(tool);
                        }
                    });
