// Auth state
let authToken = localStorage.getItem('agentforge_token') || null;
let currentUser = JSON.parse(localStorage.getItem('agentforge_user') || 'null');
let securityUsers = [];
let securityRoles = [];
// ============================================================================
// PERMISSION SYSTEM
